function calculate_stats(){G=$("games").value,AB=$("atBats").value,H=$("hits").value,R=$("runs").value,DB=$("doubles").value,TB=$("triples").value,HR=$("homeruns").value,RBI=$("rbi").value,K=$("strikeouts").value,BB=$("walks").value,HBP=$("hbp").value,SF=$("sf").value;var a=H/AB,b=Math.ceil(1e3*a)/1e3,c=(H+BB+HBP)/(AB+BB+HBP+SF),d=Math.ceil(1e3*c)/1e3;alert(b,d)}var $=function(a){return document.getElementById(a)};window.onload=function(){$("calculate").onclick=calculate_stats};