function calculate_stats(){document.getElementById("results").classList.add("show"),G=+$("games").value,AB=+$("atBats").value,H=+$("hits").value,R=+$("runs").value,DB=+$("doubles").value,TB=+$("triples").value,HR=+$("homeruns").value,RBI=+$("rbi").value,SB=+$("sb").value,K=+$("strikeouts").value,BB=+$("walks").value,HBP=+$("hbp").value,SF=+$("sf").value,BA_raw=H/AB,BA=Math.ceil(1e3*BA_raw)/1e3;var a=(H+BB+HBP)/(AB+BB+HBP+SF),b=Math.ceil(1e3*a)/1e3,c=H-DB-TB-HR,d=c+2*DB+3*TB+4*HR,e=d/AB,f=Math.ceil(1e3*e)/1e3,g=b+f,h=Math.ceil(1e3*g)/1e3;$("G_Out").innerHTML=G,$("AB_Out").innerHTML=AB,$("H_Out").innerHTML=H,$("R_Out").innerHTML=R,$("DB_Out").innerHTML=DB,$("TB_Out").innerHTML=TB,$("HR_Out").innerHTML=HR,$("RBI_Out").innerHTML=RBI,$("SB_Out").innerHTML=SB,$("K_Out").innerHTML=K,$("BB_Out").innerHTML=BB,$("HBP_Out").innerHTML=HBP,$("SF_Out").innerHTML=SF,$("BA_Out").innerHTML=BA,$("OBP_Out").innerHTML=b,$("SLG_Out").innerHTML=f,$("OPS_Out").innerHTML=h}var $=function(a){return document.getElementById(a)};window.onload=function(){$("calculate").onclick=calculate_stats};